import { useState } from "react";

function Search() {
  const [destination, setDestination] = useState("");
  const [accommodation, setAccommodation] = useState("Hotel");
  const [travelMode, setTravelMode] = useState("Flight");
  const [budget, setBudget] = useState("Medium");
  const [recommendation, setRecommendation] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const res = await fetch("http://localhost:5000/recommend", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ destination, accommodation, travelMode, budget }),
    });

    const data = await res.json();
    setRecommendation(data);
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Travel Planner</h1>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Enter destination"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
          className="border p-2 w-full"
        />

        <select
          value={accommodation}
          onChange={(e) => setAccommodation(e.target.value)}
          className="border p-2 w-full"
        >
          <option>Hotel</option>
          <option>Hostel</option>
          <option>Homestay</option>
          <option>Resort</option>
        </select>

        <select
          value={travelMode}
          onChange={(e) => setTravelMode(e.target.value)}
          className="border p-2 w-full"
        >
          <option>Flight</option>
          <option>Train</option>
          <option>Bus</option>
        </select>

        <select
          value={budget}
          onChange={(e) => setBudget(e.target.value)}
          className="border p-2 w-full"
        >
          <option>Low</option>
          <option>Medium</option>
          <option>High</option>
        </select>

        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
          Get Recommendations
        </button>
      </form>

      {recommendation && (
        <div className="mt-6 space-y-6">
          <div>
            <h3 className="text-xl font-semibold mb-2">Places</h3>
            <ul className="list-disc ml-5 space-y-1">
              {recommendation.places?.map((p, i) => (
                <li key={i}>
                  <strong>{p.name}</strong>: {p.description}
                </li>
              ))}
            </ul>
          </div>

          <div>
            <h3 className="text-xl font-semibold mb-2">Accommodation</h3>
            <ul className="list-disc ml-5 space-y-1">
              {recommendation.accommodation?.map((a, i) => (
                <li key={i}>
                  <strong>{a.name}</strong>: {a.reason}
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

export default SearchBar;

