import React, { useState } from "react";
import "./App.css";
import axios from "axios";

export const SearchBar = () => {
  const [destination, setDestination] = useState("");
  const [accommodation, setAccommodation] = useState("");
  const [travelMode, setTravelMode] = useState("");
  const [days, setDays] = useState(""); // user input for itinerary
  const [recommendation, setRecommendation] = useState(null);
  const [itinerary, setItinerary] = useState(null);
  const [activeTab, setActiveTab] = useState("places");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const response = await axios.post("http://localhost:5000/recommend", {
        destination,
        accommodation,
        travelMode,
      });
      setRecommendation(response.data);
      setItinerary(null); // reset itinerary when new search
    } catch (err) {
      console.error("Error fetching recommendations:", err);
      setError("Could not fetch recommendations. Please try again.");
    }
  };

  const handleItinerary = async () => {
    setError("");
    try {
      const response = await axios.post("http://localhost:5000/itinerary", {
        destination,
        days,
      });
      setItinerary(response.data.itinerary);
      setActiveTab("itinerary");
    } catch (err) {
      console.error("Error fetching itinerary:", err);
      setError("Could not generate itinerary. Please try again.");
    }
  };

  return (
    <div>
      {/* Search Form */}
      <form onSubmit={handleSubmit} className="searchBar">
        <label>Destination</label>
        <div className="search-1">
          <input
            type="text"
            placeholder="Enter Destination"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
          />
        </div>

        <label>Accommodation Type</label>
        <div className="search-1">
          <select
            value={accommodation}
            onChange={(e) => setAccommodation(e.target.value)}
          >
            <option value="">Select type</option>
            <option>Hotel</option>
            <option>Guesthouse</option>
            <option>Resort</option>
            <option>Bed & Breakfast</option>
            <option>Villa</option>
            <option>Motel</option>
            <option>Homestay</option>
          </select>
        </div>

        <label>Travel Mode</label>
        <div className="search-1">
          <select
            value={travelMode}
            onChange={(e) => setTravelMode(e.target.value)}
          >
            <option value="">Select mode</option>
            <option>Flight</option>
            <option>Train</option>
            <option>Bus</option>
            <option>Car / Taxi</option>
            <option>Bike / Scooter</option>
            <option>Metro / Subway</option>
            <option>Walk</option>
          </select>
        </div>

        <button type="submit">Get Recommendations</button>
      </form>

      {error && <p className="error">{error}</p>}

      <br />
      {recommendation && (
        <div className="recommendation-box">
          {/* Tabs */}
          <div className="tabs">
            <button
              className={activeTab === "places" ? "active" : ""}
              onClick={() => setActiveTab("places")}
            >
              Places
            </button>
            <button
              className={activeTab === "accommodation" ? "active" : ""}
              onClick={() => setActiveTab("accommodation")}
            >
              Accommodation
            </button>
            <button
              className={activeTab === "itinerary" ? "active" : ""}
              onClick={() => setActiveTab("itinerary")}
            >
              Itinerary
            </button>
          </div>

          {/* Places */}
          {activeTab === "places" && (
            <div className="places">
              <h2>Places to Visit</h2>
              {recommendation.places?.length > 0 ? (
                <ul>
                  {recommendation.places.map((r, i) => (
                    <li key={i}>
                      <h3>{r.name}</h3>
                      <p>{r.description}</p>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No places found for this destination.</p>
              )}
            </div>
          )}

          {/* Accommodation */}
          {/* Accommodation */}
{activeTab === "accommodation" && (
  <div className="accommodation">
    <h2>Accommodation Options</h2>
    {recommendation.accommodation?.length > 0 ? (
      <div className="accommodation-grid">
        {recommendation.accommodation.map((r, i) => (
          <div className="accommodation-card" key={i}>
            <h3>
              {r.name}{" "}
              <span className="accommodation-type">{r.type}</span>
            </h3>
            {r.reason ? (
              <p className="error">{r.reason}</p>
            ) : (
              <>
                <p>{r.description || "No description available"}</p>
                {r.lat && r.lon ? (
                  <p>
                    üìç{" "}
                    <a
                      href={`https://www.openstreetmap.org/?mlat=${r.lat}&mlon=${r.lon}#map=15/${r.lat}/${r.lon}`}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      View on Map ({r.lat.toFixed(3)}, {r.lon.toFixed(3)})
                    </a>
                  </p>
                ) : (
                  <p>üìç Location unavailable</p>
                )}
              </>
            )}
          </div>
        ))}
      </div>
    ) : (
      <p>No {accommodation || "accommodation"} options found. Try another destination or type.</p>
    )}
  </div>
)}
          {/* {activeTab === "accommodation" && (
            <div className="accommodation">
              <h2>Accommodation Options</h2>
              {recommendation.accommodation?.length > 0 ? (
                <div className="accommodation-grid">
                  {recommendation.accommodation.map((r, i) => (
                    <div className="accommodation-card" key={i}>
                      <h3>
                        {r.name}{" "}
                        <span className="accommodation-type">{r.type}</span>
                      </h3>
                      <p>
                        üìç{" "}
                        <a
                          href={`https://www.google.com/maps?q=${r.lat},${r.lon}`}
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          {r.lat}, {r.lon}
                        </a>
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <p>
                  No {accommodation || "accommodation"} options found. Showing
                  nearest hotels instead.
                </p>
              )}
            </div>
          )} */}

          {/* Itinerary */}
          {activeTab === "itinerary" && (
            <div className="itinerary">
              <h2>Itinerary Planner</h2>
              <div className="planner">
                <input
                  type="number"
                  placeholder="Enter number of days"
                  value={days}
                  onChange={(e) => setDays(e.target.value)}
                />
              </div>

                <button onClick={handleItinerary}>Generate Itinerary</button>

              {itinerary && (
                <div className="itinerary-timeline">
                  {itinerary.map((day, i) => (
                    <div className="itinerary-item" key={i}>
                      <p className="itinerary-day">
                        <strong>
                          Day {day.day}: {day.activity}
                        </strong>
                      </p>
                      <em>{day.details}</em>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
};
