import React, { useState } from "react";
import "./App.css";
import axios from "axios";

export const SearchBar = () => {
  const [destination, setDestination] = useState("");
  const [accommodation, setAccommodation] = useState("");
  const [travelMode, setTravelMode] = useState("");
  const [days, setDays] = useState(""); // user input for itinerary
  const [recommendation, setRecommendation] = useState(null);
  const [itinerary, setItinerary] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/recommend", {
        destination,
        accommodation,
        travelMode,
      });
      setRecommendation(response.data);
      setItinerary(null); // reset itinerary when new search
    } catch (error) {
      console.error("Error fetching recommendations:", error);
    }
  };

  const handleItinerary = async () => {
    try {
      const response = await axios.post("http://localhost:5000/itinerary", {
        destination,
        days,
      });
      setItinerary(response.data.itinerary);
    } catch (error) {
      console.error("Error fetching itinerary:", error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className="searchBar">
        <label>Destination</label>
        <div className="search-1">
          <input
            type="text"
            placeholder="Enter Destination"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
          />
        </div>

        <label>Accommodation Type</label>
        <div className="search-1">
          <select
            value={accommodation}
            onChange={(e) => setAccommodation(e.target.value)}
          >
            <option value="">Select type</option>
            <option>Hotel</option>
            <option>Hostel</option>
            <option>Guesthouse</option>
            <option>Resort</option>
            <option>Apartment</option>
            <option>Bed & Breakfast</option>
            <option>Villa</option>
            <option>Motel</option>
            <option>Homestay</option>
          </select>
        </div>

        <label>Travel Mode</label>
        <div className="search-1">
          <select
            value={travelMode}
            onChange={(e) => setTravelMode(e.target.value)}
          >
            <option value="">Select mode</option>
            <option>Flight</option>
            <option>Train</option>
            <option>Bus</option>
            <option>Car / Taxi</option>
            <option>Bike / Scooter</option>
            <option>Metro / Subway</option>
            <option>Walk</option>
          </select>
        </div>

        <button type="submit">Get Recommendations</button>
      </form>

      <br />
      {recommendation && (
        <div className="recommendation-box">
          {/* Places */}
          <h1>Places</h1>
          <ul>
            {recommendation.places?.map((r, i) => (
              <li key={i}>
                <h2>{r.name}</h2>
                <p>{r.description}</p>
              </li>
            ))}
          </ul>

          {/* Accommodation */}
          <h1>Accommodation</h1>
          <div className="accommodation-grid">
            {recommendation.accommodation?.map((r, i) => (
              <div className="accommodation-card" key={i}>
                <h2>
                  {r.name}{" "}
                  <span className="accommodation-type">{r.type}</span>
                </h2>
                <p>📍 {r.lat}, {r.lon}</p>
              </div>
            ))}
          </div>

          {/* Itinerary Planner */}
          <h1>Itinerary Planner</h1>
          <div className="search-itinerary">
            <input
              type="number"
              placeholder="Enter number of days"
              value={days}
              onChange={(e) => setDays(e.target.value)}
            />
          </div>
            <button onClick={handleItinerary}>Generate Itinerary</button>

          {itinerary && (
            <div className="itinerary">
              {itinerary.map((day, i) => (
                <div className="itinerary-item" key={i}>
                  <p className="itinerary-day">
                    <strong>Day {day.day}: {day.activity}</strong>
                  </p>
                  <em>{day.details}</em>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};
