import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

export const SearchBar = () => {
  const [destination, setDestination] = useState('');
  const [accommodation, setAccommodation] = useState('');
  const [travelMode, setTravelMode] = useState('');
  const [recommendation, setRecommendation] = useState(''); // new state

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/recommend', {
        destination,
        accommodation,
        travelMode,
      });

      // setRecommendation(response.data); // store in state
      // console.log('Response:', response.data);
      const raw = response.data;

const formatted = {
  itinerary: raw.itinerary || "No itinerary (Gemini off)",
  justification: raw.justification || "No justification available",
  accommodation: raw.accommodation || [],
  places:raw.places || [],
  transport: raw.transport || "",
  mapPoints: raw.mapPoints || raw.pois?.map(p => ({
    name: p.name,
    latitude: p.lat,
    longitude: p.lon,
    short_description: p.short_description
  })) || []
};

setRecommendation(formatted);


    } catch (error) {
      console.error('Error sending data:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className='searchBar'>
        <label>Destination</label>
        <div className='search-1'>
          <input
            type="text"
            placeholder='Enter Destination'
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
          />
        </div>
<label>Accommodation Type</label>
        <div className='search-1'>
          <select value={accommodation} onChange={(e) => setAccommodation(e.target.value)}>
            <option value="">Select type</option>
            <option>Hotel</option>
            <option>Hostel</option>
            <option>Guesthouse</option>
            <option>Resort</option>
            <option>Apartment</option>
            <option>Bed & Breakfast</option>
            <option>Villa</option>
            <option>Motel</option>
            <option>Homestay</option>
          </select>
        </div>
        

        <label>Travel Mode</label>
        <div className='search-1'>
          <select value={travelMode} onChange={(e) => setTravelMode(e.target.value)}>
            <option value="">Select mode</option>
            <option>Flight</option>
            <option>Train</option>
            <option>Bus</option>
            <option>Car / Taxi</option>
            <option>Bike / Scooter</option>
            <option>Metro / Subway</option>
            <option>Walk</option>
          </select>
        </div>

        <button type="submit">Get Recommendations</button>
      </form>

      <br />
      {recommendation && (
        <div className='recommendation-box'>
     {recommendation.mapPoints?.map((p, i) => (
    <p key={i}>
      <h3>{p.name}</h3>
      <p>{p.short_description}</p>
      </p>
      ))}
      <h3>Itinerary</h3>
<p>{recommendation.itinerary}</p>

<h3>Justification</h3>
<p>{recommendation.justification}</p>

<h3>Restaurants</h3>
<ul>
  {recommendation.restaurants?.map((r, i) => (
    <li key={i}>
      <strong>{r.name}</strong>: {r.reason}
    </li>
  ))}
</ul> 

<h3>Transport</h3>
<p>{recommendation.transport}</p>

<h3>Map Points</h3>
<ul>
  {recommendation.mapPoints?.map((p, i) => (
    <li key={i}>
      <b>{p.name}</b> ({p.latitude}, {p.longitude}) <br />
      {/* {p.short_description} */}
    </li>
  ))}
</ul>
        </div>
      )}
    </div>
  );
};
